mapper.select:
select

from hs_cang_chuku

    and deleted = 0
    and orderId = #{orderId,jdbcType=BIGINT}

    and hsId = #{hsId, jdbcType=BIGINT}
    and chukuDate = #{chukuDate, jdbcType=TIMESTAMP}
    and chukuPrice = #{chukuPrice, jdbcType=DECIMAL}
    and chukuAmount= #{chukuAmount, jdbcType=DECIMAL}

mapper.update:
update hs_cang_chuku
orderId = #{orderId,jdbcType=BIGINT}, hsId = #{hsId,jdbcType=BIGINT}, chukuDate = #{chukuDate,jdbcType=TIMESTAMP}, locality = #{locality,jdbcType=VARCHAR}, chukuAmount = #{chukuAmount,jdbcType=DECIMAL}, chukuPrice = #{chukuPrice,jdbcType=DECIMAL}, tsc = #{tsc,jdbcType=TIMESTAMP},
where id = #{id,jdbcType=BIGINT}
