mapper.select:
select

FROM hs_same_fee

    deleted = 0
    AND orderId = #{orderId, jdbcType=BIGINT}
    AND quantity = #{quantity, jdbcType=DECIMAL}
    AND otherPartyId = #{otherPartyId, jdbcType=BIGINT}
    AND hsId = #{hsId, jdbcType=BIGINT}
    AND name = #{name, jdbcType=VARCHAR}
    AND amount= #{amount, jdbcType=VARCHAR}

mapper.update:
update hs_same_fee
orderId = #{orderId,jdbcType=BIGINT}, hsId = #{hsId,jdbcType=BIGINT}, name = #{name,jdbcType=VARCHAR}, amount = #{amount,jdbcType=DECIMAL}, otherPartyId = #{otherPartyId,jdbcType=BIGINT}, quantity = #{quantity,jdbcType=DECIMAL}, tsc = #{tsc,jdbcType=TIMESTAMP}, tsu = current_timestamp,
where id = #{id,jdbcType=BIGINT}
