mapper.select:
SELECT id , orderId, hsId , huikuanCompanyId, huikuanDate, huikuanAmount, huikuanUsage, huikuanMode, huikuanBankPaper, huikuanBankPaperDate, huikuanBankDiscount, huikuanBankDiscountRate, huikuanBankPaperExpire, huikuanBusinessPaper, huikuanBusinessPaperDate, huikuanBusinessDiscount, huikuanBusinessDiscountRate, huikuanBusinessPaperExpire, tsc
FROM hs_same_huikuan
deleted = 0 AND orderId=#{orderId} AND hsId=#{hsId} AND huikuanCompanyId=#{huikuanCompanyId} AND huikuanMode=#{huikuanMode} AND huikuanDate=#{huikuanDate}

mapper.update:
UPDATE hs_same_huikuan
SET deleted = 1, tsu = current_timestamp
WHERE id = #{id,jdbcType=BIGINT}
