mapper.select:
SELECT
t1.id,
t1.orderId,
t1.hsId,
t1.invoiceDirection,
t1.invoiceType,
t1.openDate,
t1.openCompanyId,
t1.receiverId,
t1.tsc,
t1.tsu
FROM hs_same_invoice t1

    AND t1.deleted = 0
    AND orderId=#{orderId,jdbcType=BIGINT}
    AND hsId=#{hsId,jdbcType=BIGINT}
    AND openCompanyId=#{openCompanyId,jdbcType=BIGINT}
    AND invoiceType=#{invoiceType,jdbcType=VARCHAR}
    AND receiverId=#{receiverId, jdbcType=BIGINT}
    AND invoiceDirection=#{invoiceDirection, jdbcType=BIGINT}
    AND openDate=#{openDate, jdbcType=TIMESTAMP}
         AND DATE_FORMAT(tsc, '%Y-%m-%d')>=  DATE_FORMAT(#{openDateStart}, '%Y-%m-%d')   
          AND DATE_FORMAT(tsc, '%Y-%m-%d') <= DATE_FORMAT(#{openDateEnd}, '%Y-%m-%d')

mapper.update:
update hs_same_invoice
orderId = #{orderId,jdbcType=BIGINT}, hsId = #{hsId,jdbcType=BIGINT}, invoiceDirection = #{invoiceDirection,jdbcType=VARCHAR}, invoiceType = #{invoiceType,jdbcType=VARCHAR}, openDate = #{openDate,jdbcType=TIMESTAMP}, openCompanyId = #{openCompanyId,jdbcType=BIGINT}, receiverId = #{receiverId,jdbcType=BIGINT}, tsc = #{tsc,jdbcType=TIMESTAMP}, tsu = current_timestamp,
where id = #{id,jdbcType=BIGINT}
