mapper.select:
select
from hs_same_order_party
AND deleted = 0 AND orderId = #{orderId, jdbcType=BIGINT}

mapper.update:
update hs_same_order_party
orderId = #{orderId,jdbcType=BIGINT}, custType = #{custType,jdbcType=VARCHAR}, customerId = #{customerId,jdbcType=BIGINT}, tsc = #{tsc,jdbcType=TIMESTAMP}, tsu = current_timestamp,
where id = #{id,jdbcType=BIGINT}
