mapper.select:
select

FROM hs_same_settle_buyer

    AND deleted = 0
    AND orderId = #{orderId, jdbcType=BIGINT}
    AND hsId = #{hsId, jdbcType=BIGINT}
    AND settleDate = #{settleDate, jdcbType=TIMESTAMP}

mapper.update:
UPDATE hs_same_settle_buyer
SET deleted = 1, tsu = current_timestamp
WHERE id = #{id,jdbcType=BIGINT}

update hs_same_settle_buyer
orderId = #{orderId,jdbcType=BIGINT}, hsId = #{hsId,jdbcType=BIGINT}, settleDate = #{settleDate,jdbcType=TIMESTAMP}, amount = #{amount,jdbcType=DECIMAL}, money = #{money,jdbcType=DECIMAL}, settleGap = #{settleGap,jdbcType=DECIMAL}, tsc = #{tsc,jdbcType=TIMESTAMP}, tsu = #{tsu,jdbcType=TIMESTAMP}, tsu = current_timestamp,
where id = #{id,jdbcType=BIGINT}
