mapper.select:
select

sum(buyer.amount),
sum(buyer.money),
sum(buyer.settleGap)
FROM hs_same_settle_seller

    AND orderId=#{orderId,jdbcType=BIGINT}
    AND hsId= #{hsId,jdbcType=BIGINT}
    AND settleDate=#{settleDate,jdbcType=DATE}

group by seller.orderId

mapper.update:
UPDATE hs_same_settle_seller
SET deleted = 1, tsu = current_timestamp
WHERE id = #{id,jdbcType=BIGINT};

update hs_same_settle_seller
orderId = #{orderId,jdbcType=BIGINT}, hsId = #{hsId,jdbcType=BIGINT}, settleDate = #{settleDate,jdbcType=DATE}, discountType = #{discountType,jdbcType=VARCHAR}, discountInterest = #{discountInterest,jdbcType=DECIMAL}, discountDays = #{discountDays,jdbcType=INTEGER}, discountAmount = #{discountAmount,jdbcType=DECIMAL}, tsc = #{tsc,jdbcType=TIMESTAMP}, tsu = current_timestamp,
where id = #{id,jdbcType=BIGINT}
