mapper.select:
select

from hs_user

      AND isAdmin = #{isAdmin, jdbcType=TINYINT}
      AND isActive = #{isActive, jdbcType=TINYINT}
      AND deptId = #{deptId, jdbcType=BIGINT}

mapper.update:
update hs_user
deptId = #{deptId,jdbcType=BIGINT}, phone = #{phone,jdbcType=VARCHAR}, password = #{password,jdbcType=VARCHAR}, passwordSalt = #{passwordSalt,jdbcType=VARCHAR}, createDate = #{createDate,jdbcType=TIMESTAMP}, createBy = #{createBy,jdbcType=VARCHAR}, isAdmin = #{isAdmin,jdbcType=BIT}, isActive = #{isActive,jdbcType=BIT},
where id = #{id,jdbcType=BIGINT}
