mapper.select:
select
id,
orderId,
hsId,
bailDate,
bailAmount,
bailType,
receiverId,
openCompanyId,
tsc,
tsu
FROM hs_ying_bail

    and deleted = 0
    and orderId = #{orderId,jdbcType=BIGINT}
    and bailDate = #{bailDate, jdbcType=TIMESTAMP}
    and bailType= #{bailType, jdbcType=VARCHAR}
    and bailAmount= #{bailAmount, jdbcType=DECIMAL}
    and hsId = #{hsId, jdbcType=VARCHAR}
    and receiverId= #{receiverId, jdbcType=BIGINT}
    and openCompanyId = #{openCompanyId, jdbcType=BIGINT}
         AND DATE_FORMAT(bailDate, '%Y-%m-%d')>=  DATE_FORMAT(#{bailDateStart}, '%Y-%m-%d')   
          AND DATE_FORMAT(bailDate, '%Y-%m-%d') <= DATE_FORMAT(#{bailDateEnd}, '%Y-%m-%d')

mapper.update:
update hs_ying_bail
orderId = #{orderId,jdbcType=BIGINT}, hsId = #{hsId,jdbcType=BIGINT}, bailDate = #{bailDate,jdbcType=DATE}, bailAmount = #{bailAmount,jdbcType=DECIMAL}, bailType = #{bailType,jdbcType=VARCHAR}, receiverId= #{receiverId,jdbcType=BIGINT}, openCompanyId= #{openCompanyId,jdbcType=BIGINT}, tsu = current_timestamp,
where id = #{id,jdbcType=BIGINT}
