mapper.select:
select

FROM hs_ying_fee

    deleted = 0
    AND orderId = #{orderId, jdbcType=BIGINT}
    AND hsId = #{hsId, jdbcType=BIGINT}
    AND name = #{name, jdbcType=VARCHAR}

mapper.update:
UPDATE hs_ying_fee
SET deleted = 1, tsu = current_timestamp
WHERE id = #{id,jdbcType=BIGINT}

update hs_ying_fee
orderId = #{orderId,jdbcType=BIGINT}, hsId = #{hsId,jdbcType=BIGINT}, name = #{name,jdbcType=VARCHAR}, amount = #{amount,jdbcType=DECIMAL}, tsc = #{tsc,jdbcType=TIMESTAMP}, tsu = current_timestamp,
where id = #{id,jdbcType=BIGINT}
