mapper.select:
select t1.id, t1.orderId, t1.hsId, t1.payDate, t1.receiveCompanyId, t1.payUsage, t1.payAmount, t1.payMode, t1.capitalId, t1.useInterest, t1.useDays, t1.tsc, t1.tsu from hs_ying_fukuan t1 LEFT JOIN hs_ying_huankuan_map map on t1.id = map.fukuanId
t1.deleted = 0 and t1.orderId = #{orderId, jdbcType=BIGINT} and t1.hsId = #{hsId, jdbcType=BIGINT} and t1.payDate = #{payDate, jdbcType=TIMESTAMP} and t1.receiveCompanyId = #{receiveCompanyId, jdbcType=BIGINT}
order by t1.payDate asc

mapper.update:
UPDATE hs_ying_fukuan
SET deleted = 1, tsu = current_timestamp
WHERE id = #{id,jdbcType=BIGINT}

update hs_ying_fukuan
orderId = #{orderId,jdbcType=BIGINT}, hsId = #{hsId,jdbcType=BIGINT}, payDate = #{payDate,jdbcType=TIMESTAMP}, receiveCompanyId = #{receiveCompanyId,jdbcType=BIGINT}, payUsage = #{payUsage,jdbcType=VARCHAR}, payAmount = #{payAmount,jdbcType=DECIMAL}, payMode = #{payMode,jdbcType=VARCHAR}, capitalId = #{capitalId,jdbcType=BIGINT}, useInterest = #{useInterest,jdbcType=DECIMAL}, useDays = #{useDays,jdbcType=INTEGER}, tsc = #{tsc,jdbcType=TIMESTAMP}, tsu = current_timestamp,
where id = #{id,jdbcType=BIGINT}
