mapper.select:
select

from hs_ying_fukuan

    orderId = #{orderId, jdbcType=BIGINT}
    hsId = #{hsId, jdbcType=BIGINT}
    payDate = #{payDate, jdbcType=TIMESTAMP}
    recieveCompanyId = #{recieveCompanyId, jdbcType=BIGINT}

mapper.update:
update hs_ying_fukuan
orderId = #{orderId,jdbcType=BIGINT}, hsId = #{hsId,jdbcType=BIGINT}, payDate = #{payDate,jdbcType=TIMESTAMP}, recieveCompanyId = #{recieveCompanyId,jdbcType=BIGINT}, payUsage = #{payUsage,jdbcType=VARCHAR}, payAmount = #{payAmount,jdbcType=DECIMAL}, payMode = #{payMode,jdbcType=VARCHAR}, capitalId = #{capitalId,jdbcType=BIGINT}, useInterest = #{useInterest,jdbcType=DECIMAL}, useDays = #{useDays,jdbcType=INTEGER}, tsc = #{tsc,jdbcType=TIMESTAMP},
where id = #{id,jdbcType=BIGINT}
