mapper.select:
select

from hs_ying_settle_traffic

    deleted = 0
    AND orderId=#{orderId,jdbcType=BIGINT}
    AND hsId=#{hsId,jdbcType=BIGINT}
    AND settleDate= #{settleDate,jdbcType=DATE}

mapper.update:
UPDATE hs_ying_settle_traffic
SET deleted = 1
WHERE id = #{id,jdbcType=BIGINT}

update hs_ying_settle_traffic
orderId = #{orderId,jdbcType=BIGINT}, hsId = #{hsId,jdbcType=BIGINT}, settleDate = #{settleDate,jdbcType=DATE}, amount = #{amount,jdbcType=DECIMAL}, money = #{money,jdbcType=DECIMAL}, trafficCompanyId = #{trafficCompanyId,jdbcType=BIGINT}, tsc = #{tsc,jdbcType=TIMESTAMP},
where id = #{id,jdbcType=BIGINT}
